mapper.select:
select 

from mmall_cart
where user_id = #{userId,jdbcType=INTEGER}
and product_id = #{productId,jdbcType=INTEGER}

mapper.delete:
delete from mmall_cart
where user_id = #{userId,jdbcType=INTEGER}
and
product_id in

	#{item}

mapper.update:
update mmall_cart
set
  
    quantity = #{quantity,jdbcType=INTEGER},
  
  
    checked = 1
  
  
    checked = #{checked,jdbcType=INTEGER}
  
where user_id = #{userId,jdbcType=INTEGER} 

    and product_id = #{productId,jdbcType=INTEGER}
