mapper.select:
select

from mmall_product
WHERE
1=1

  and id = #{productId,jdbcType=INTEGER}


  and status = #{status,jdbcType=INTEGER}


  and name like concat('%', #{productName,jdbcType=VARCHAR}, '%')


  and name like concat('%', #{keyword,jdbcType=VARCHAR}, '%')


  and category_id = #{categoryId,jdbcType=INTEGER}


  and update_time  >=  #{beginTime,jdbcType=TIMESTAMP}


  and update_time  <=  #{endTime,jdbcType=TIMESTAMP}


  order by ${orderBy}
